package com.zoe.diary.utils;import android.content.Context;import android.content.SharedPreferences;import android.util.Log;import com.zoe.diary.constant.Constants;/** * Created by victor on 2016/7/15. */public class SharePreferencesUtil {    private static String TAG = "SharePreferencesUtil";    public static void putInt(Context context, String key, int value) {        Log.i(TAG, "putInt()-" + key + "=" + value);        SharedPreferences sp = context.getSharedPreferences(Constants.DIARY_SP_NAME, Context.MODE_PRIVATE);        SharedPreferences.Editor ed = sp.edit();        ed.putInt(key, value);        ed.commit();    }    public static int getInt(Context context, String key) {        SharedPreferences sp = context.getSharedPreferences(Constants.DIARY_SP_NAME, Context.MODE_PRIVATE);        int value = sp.getInt(key, 0);        Log.i(TAG, "putInt()-" + key + "=" + value);        return value;    }    public static void putLong(Context context, String key, long value) {        Log.i(TAG, "putLong()-" + key + "=" + value);        SharedPreferences sp = context.getSharedPreferences(Constants.DIARY_SP_NAME, Context.MODE_PRIVATE);        SharedPreferences.Editor ed = sp.edit();        ed.putLong(key, value);        ed.commit();    }    public static Long getLong(Context context, String key) {        SharedPreferences sp = context.getSharedPreferences(Constants.DIARY_SP_NAME, Context.MODE_PRIVATE);        Long value = sp.getLong(key, 0);        Log.i(TAG, "getLong()-" + key + "=" + value);        return value;    }    public static void putString(Context context, String key, String value) {        Log.i(TAG, "putString()-" + key + "=" + value);        SharedPreferences sp = context.getSharedPreferences(Constants.DIARY_SP_NAME, Context.MODE_PRIVATE);        SharedPreferences.Editor ed = sp.edit();        ed.putString(key, value);        ed.commit();    }    public static String getString(Context context, String key, String defaultValue) {        SharedPreferences sp = context.getSharedPreferences(Constants.DIARY_SP_NAME, Context.MODE_PRIVATE);        String value = sp.getString(key, defaultValue);        Log.i(TAG, "getString()-" + key + "=" + value);        return value;    }    public static void putBoolean(Context context, String key, boolean value) {        Log.i(TAG, "putBoolean()-" + key + "=" + value);        SharedPreferences sp = context.getSharedPreferences(Constants.DIARY_SP_NAME, Context.MODE_PRIVATE);        SharedPreferences.Editor ed = sp.edit();        ed.putBoolean(key, value);        ed.commit();    }    public static boolean getBoolean(Context context, String key) {        SharedPreferences sp = context.getSharedPreferences(Constants.DIARY_SP_NAME, Context.MODE_PRIVATE);        boolean value = sp.getBoolean(key, false);        Log.i(TAG, "getBoolean()-" + key + "=" + value);        return value;    }}